import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Box,
  Button,
  CircularProgress
} from '@mui/material';
import { topicService } from '../../services/topicService';
import CodeEditor from '../Common/CodeEditor';

const TopicDetail = () => {
  const { id } = useParams();
  const [topic, setTopic] = useState(null);
  const [solution, setSolution] = useState('// Write your solution here\n\n');
  const [requirements, setRequirements] = useState('// Define your requirements here\n\n');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTopic = async () => {
      try {
        setLoading(true);
        const data = await topicService.getTopicById(id);
        setTopic(data);
      } catch (error) {
        console.error('Error fetching topic:', error);
        setError('Failed to load topic details');
      } finally {
        setLoading(false);
      }
    };

    fetchTopic();
  }, [id]);

  const handleSubmit = async () => {
    try {
      const response = await topicService.submitSolution(id, {
        solution,
        requirements
      });
      console.log(response.data);
    } catch (error) {
      console.error('Error submitting solution:', error);
    }
  };

  if (loading) {
    return (
      <Container sx={{ py: 4, textAlign: 'center' }}>
        <CircularProgress />
      </Container>
    );
  }

  if (error) {
    return (
      <Container sx={{ py: 4 }}>
        <Typography color="error">{error}</Typography>
      </Container>
    );
  }

  return (
    <Container sx={{ py: 4 }}>
      {topic && (
        <>
          <Typography variant="h4" gutterBottom>
            {topic.name}
          </Typography>
          <Paper sx={{ p: 3, mb: 3 }}>
            <Typography variant="h6" gutterBottom>Problem Statement</Typography>
            <Typography sx={{ whiteSpace: 'pre-line' }}>{topic.description}</Typography>
          </Paper>

          <Paper sx={{ p: 3, mb: 3 }}>
            <Typography variant="h6" gutterBottom>
              Functional Requirements
            </Typography>
            <CodeEditor
              value={requirements}
              onChange={setRequirements}
              height="200px"
              language="javascript"
            />
          </Paper>

          <Paper sx={{ p: 3, mb: 3 }}>
            <Typography variant="h6" gutterBottom>
              Solution
            </Typography>
            <CodeEditor
              value={solution}
              onChange={setSolution}
              height="400px"
              language="javascript"
            />
          </Paper>

          <Box sx={{ mt: 2 }}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleSubmit}
              sx={{
                bgcolor: '#ffa116',
                '&:hover': {
                  bgcolor: '#ff9100'
                }
              }}
            >
              Submit Solution
            </Button>
          </Box>
        </>
      )}
    </Container>
  );
};

export default TopicDetail;
