import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  CardActionArea,
  Box
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { topicService } from '../../services/topicService';

const TopicList = () => {
  const [topics, setTopics] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTopics = async () => {
      try {
        console.log('Fetching topics...');
        const data = await topicService.getAllTopics();
        setTopics(Array.isArray(data) ? data : []);
      } catch (error) {
        console.error('Error fetching topics:', error);
        setTopics([]);
      }
    };

    fetchTopics();
  }, []);

  return (
    <Container sx={{ py: 4 }}>
      <Typography 
        variant="h4" 
        gutterBottom 
        sx={{ 
          color: '#2c3e50',
          mb: 4,
          textAlign: 'center',
          fontWeight: 'bold'
        }}
      >
        Machine Coding Practice Topics
      </Typography>
      <Grid container spacing={3}>
        {topics.map((topic) => (
          <Grid item xs={12} sm={6} md={4} key={topic.id}>
            <Card 
              sx={{ 
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                transition: 'transform 0.2s ease-in-out',
                '&:hover': {
                  transform: 'translateY(-5px)',
                  boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
                },
                borderRadius: 2,
                bgcolor: '#ffffff',
                minHeight: '280px' // Ensures consistent card height
              }}
            >
              <CardActionArea 
                onClick={() => navigate(`/topics/${topic.id}`)}
                sx={{ 
                  flexGrow: 1,
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'stretch',
                  height: '100%'
                }}
              >
                <CardContent sx={{ 
                  flexGrow: 1,
                  display: 'flex',
                  flexDirection: 'column',
                  p: 3
                }}>
                  <Box sx={{ mb: 2 }}>
                    <Typography 
                      variant="h6" 
                      sx={{ 
                        color: '#1a73e8',
                        fontWeight: 'bold',
                        mb: 1
                      }}
                    >
                      {topic.name}
                    </Typography>
                    <Typography 
                      sx={{ 
                        color: '#5f6368',
                        mb: 2,
                        flexGrow: 1,
                        // Limit to 3 lines of text
                        display: '-webkit-box',
                        WebkitLineClamp: 3,
                        WebkitBoxOrient: 'vertical',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        lineHeight: 1.5
                      }}
                    >
                      {topic.description}
                    </Typography>
                  </Box>
                  <Box 
                    sx={{ 
                      mt: 'auto',
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'center'
                    }}
                  >
                    <Typography 
                      variant="body2" 
                      sx={{ 
                        color: '#34a853',
                        bgcolor: '#e6f4ea',
                        px: 2,
                        py: 0.5,
                        borderRadius: 1,
                        fontWeight: 'medium'
                      }}
                    >
                      Difficulty: Medium
                    </Typography>
                  </Box>
                </CardContent>
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default TopicList;
