import axios from 'axios';
import { API_BASE_URL } from '../config/apiConfig';

const BASE_URL = `${API_BASE_URL}/api/submissions`;

export const submissionService = {
    evaluateSolution: async (problemId, submissionData) => {
        try {
            const url = `${BASE_URL}/evaluate/${problemId}`;
            console.log('Making API call to:', url);
            console.log('With data:', submissionData);

            const response = await axios.post(url, {
                code: submissionData.code,
                functionalRequirements: submissionData.functionalRequirements || ""
            }, {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            });

            console.log('Raw API response:', response);
            console.log('Response data:', response.data);

            if (!response.data) {
                throw new Error('Empty response received from server');
            }

            // Validate the response data
            const data = response.data;
            if (!data.score && data.score !== 0) {
                console.error('Invalid response format - missing score:', data);
                throw new Error('Invalid response format from server');
            }

            return data;
        } catch (error) {
            console.error('Evaluation error:', {
                message: error.message,
                response: error.response?.data,
                status: error.response?.status,
                config: error.config // This will show the request details
            });
            throw error;
        }
    },

    executeSolution: async (submissionData) => {
        try {
            const response = await axios.post(`${BASE_URL}/execute`, submissionData);
            return response.data;
        } catch (error) {
            console.error('Execution error:', error);
            throw error;
        }
    },

    testConnection: async () => {
        try {
            const response = await axios.get(`${BASE_URL}/test`);
            return response.data;
        } catch (error) {
            console.error('Test connection error:', error);
            throw error;
        }
    }
};

export default submissionService;
