import { createTheme } from '@mui/material/styles';

// Define the new color scheme
export const appColors = {
  primary: '#6366F1', // Lighter Indigo
  primaryHover: '#4F46E5',
  primaryLight: 'rgba(99, 102, 241, 0.1)',
  secondary: '#8B5CF6', // Lighter Purple
  accent: '#A78BFA', // Even lighter purple
  gradient: {
    start: '#6366F1',
    end: '#8B5CF6'
  },
  text: {
    primary: '#1F2937',
    secondary: '#4B5563',
  }
};

export const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: appColors.primary,
    },
    background: {
      default: '#ffffff',
      paper: '#ffffff',
    },
  },
  components: {
    MuiAppBar: {
      styleOverrides: {
        root: {
          backgroundColor: appColors.primary,
          backgroundImage: 'none',
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
        },
      },
    },
  },
});
