import api from '../api/axiosConfig';

export const submissionService = {
  submitSolution: async (submissionData) => {
    const response = await api.post('/api/submissions', submissionData);
    return response.data;
  },

  evaluateSolution: async (problemId, submissionData) => {
    try {
      const response = await api.post(`/submissions/evaluate/${problemId}`, submissionData);
      return response.data;
    } catch (error) {
      if (error.response?.status === 403) {
        throw new Error('Please login to submit your solution');
      }
      throw error;
    }
  }
};

export default submissionService;
