import React from 'react';
import {
  AppBar,
  Box,
  Toolbar,
  Typography,
  Button,
  Container,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import CodeIcon from '@mui/icons-material/Code';
import { appColors } from '../../theme';

const Navbar = () => {
  const navigate = useNavigate();

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar 
        position="fixed"
        sx={{ 
          background: `linear-gradient(90deg, ${appColors.gradient.start} 0%, ${appColors.gradient.end} 100%) !important`,
          backgroundImage: 'none !important',
          boxShadow: 'none',
          borderBottom: '1px solid rgba(255,255,255,0.1)'
        }}
      >
        <Container maxWidth="lg">
          <Toolbar 
            sx={{ 
              minHeight: '60px !important',
              background: 'transparent !important'
            }}
          >
            {/* Logo and Brand */}
            <Box 
              sx={{ 
                display: 'flex', 
                alignItems: 'center',
                cursor: 'pointer'
              }}
              onClick={() => navigate('/')}
            >
              <CodeIcon sx={{ 
                color: appColors.accent,
                mr: 1,
                fontSize: '28px',
                filter: 'brightness(1.2)'
              }} />
              <Typography
                variant="h6"
                sx={{
                  fontWeight: 600,
                  fontSize: '1.25rem',
                  color: '#ffffff',
                  display: 'flex',
                  alignItems: 'center'
                }}
              >
                Machine Coding Platform
              </Typography>
            </Box>

            {/* Navigation Links */}
            <Box sx={{ ml: 4, display: 'flex', alignItems: 'center', flex: 1 }}>
              <Button
                sx={{
                  color: '#ffffff !important',
                  fontSize: '0.9rem',
                  minWidth: 'auto',
                  px: 2,
                  py: 1,
                  '&:hover': { 
                    color: `${appColors.accent} !important`,
                    backgroundColor: 'rgba(255, 255, 255, 0.1)'
                  }
                }}
                onClick={() => navigate('/topics')}
              >
                Problems
              </Button>
            </Box>

            {/* Right Side Actions */}
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Button
                sx={{
                  color: '#ffffff !important',
                  fontSize: '0.9rem',
                  '&:hover': { 
                    color: `${appColors.accent} !important`,
                    backgroundColor: 'rgba(255, 255, 255, 0.1)'
                  }
                }}
                onClick={() => navigate('/login')}
              >
                Sign In
              </Button>
              <Button
                variant="contained"
                sx={{
                  bgcolor: 'rgba(255, 255, 255, 0.15) !important',
                  color: '#ffffff !important',
                  '&:hover': { 
                    bgcolor: 'rgba(255, 255, 255, 0.25) !important',
                    boxShadow: '0 2px 8px rgba(255, 255, 255, 0.2)'
                  },
                  textTransform: 'none',
                  px: 3,
                  py: 1,
                  fontWeight: 500,
                  backdropFilter: 'blur(8px)'
                }}
                onClick={() => navigate('/register')}
              >
                Register
              </Button>
            </Box>
          </Toolbar>
        </Container>
      </AppBar>
      <Toolbar /> {/* This creates space below the fixed navbar */}
    </Box>
  );
};

export default Navbar;
